package week1.day1;


public class PositiveNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=35;
				if (i<0)
					System.out.println(i+" is a negative number.");
				else if (i>0)
				System.out.println(i+" is positive number.");
				else
					System.out.println(i+" is not a positive number or a negative number.");
	}

}
